import React, { Component } from 'react'

import SignIn from '../../containers/SignIn'
import SignUp from '../../containers/SignUp'

import login from './index.module.css'

export default class Login extends Component {
    state = {
        isSignIn: true,
    }
    constructor(props) {
        super(props)
        console.log(props);
    }

    handelClick = (node) => {
        let { isSignIn } = this.state;
        if (node.target.innerText === "SignIn") {
            if (!isSignIn) {
                this.setState({
                    isSignIn: true
                })
            }
        } else {
            if (isSignIn) {
                this.setState({
                    isSignIn: false
                })
            }
        }
    }

    render() {
        const { isSignIn } = this.state
        return (
            <div className={login.loginContainer}>
                <div className={login.title}>
                    <div className={login.titleName}>uMeet</div>
                    <div className={login.sign} onClick={this.handelClick} style={{ color: (isSignIn ? "rgb(0, 122, 204)" : "") }}>登录</div>|
                    <div className={login.sign} onClick={this.handelClick} style={{ color: (isSignIn ? "" : "rgb(0, 122, 204)") }}>注册</div>
                </div>
                {isSignIn ? <SignIn handleHistoryReplace={this.handleHistoryReplace} /> : ""}
                {!isSignIn ? <SignUp changeIsSignIn={this.changeIsSignIn} /> : ""}
                {/* <img className={login.login_pic} src={login_pic} alt="" /> */}
            </div>
        )
    }

    changeIsSignIn = () => {
        this.setState({
            isSignIn: true
        })
    }

    handleHistoryReplace = (url) => {
        this.props.history.replace(url);
    }

}
