import React, { Component } from 'react'

import signUp from './index.module.css'


export default class SignUp extends Component {
    constructor(props) {
        super(props)
        this.state = {
            userIDInvalid: false,
            userID: '',
            userName: '',
            password: '',
        }
    }


    render() {
        const { userIDInvalid } = this.state;
        return (
            <div >
                <form className={signUp.pageContainer} action="">
                    <div className={signUp.userID} >StudentID</div>
                    <input className={userIDInvalid ? signUp.userInputInvalid : signUp.userInput} placeholder="Your ID" type="text" onBlur={(e) => this.handleBlur(e, 'id')} />
                    <div className={signUp.userInfo} >Full Name</div>
                    <input className={signUp.userInput} placeholder="Your Full Name" type="text" onBlur={(e) => this.handleBlur(e, 'name')}/>
                    <div className={signUp.userInfo}>Password</div>
                    <input className={signUp.userInput} placeholder="Password" type="password" onBlur={(e) => this.handleBlur(e, 'password')} />
                    <input className={signUp.signUpButton} type="button" value="SignUp" onClick={(e)=>this.handleRegisterClick(e)}/>
                </form>
            </div>
        )
    }

    handleBlur = async (e, info) => {
        switch (info) {
            case 'id':
                if (e.target.value !== '') {
                    var response = await reqCheck({ userID: e.target.value });
                    if (!response.data.code) {
                        this.setState({
                            userIDInvalid: true
                        })
                    } else {
                        this.setState({
                            userIDInvalid: false,
                            userID: e.target.value
                        })
                    }
                }
                break;
            case 'name':
                if (e.target.value !== '') {
                    this.setState({
                        userName:e.target.value
                    })
                }
                break;
            case 'password':
                if (e.target.value !== '') {
                    this.setState({
                        password:e.target.value
                    })
                }
                break;

            default:
                break;
        }

    }

    handleRegisterClick = async (e) => {
        const {userIDInvalid, userID, password, userName} = this.state;
        if(!userIDInvalid && password && userName){
            var response = await reqRegister({userID, password, userName, type:'student'})
            if( response.data.code ){
                e.target.value = "Registed Successfully"
                setTimeout(() => {
                    this.props.changeIsSignIn();
                }, 1500);
            }else{
                console.log(response.data);
            }
        }else{
            console.log('else happend',userIDInvalid ,' 5 ',password,' 5 ', userName);
        }
    }

}
