import React, { Component } from 'react'
import MainSheet from './index.module.css'
import '../../App.css'

import Login from '../../components/Login'

export default class NavigationBar extends Component {
    state = {
        login : false
    }
    // constructor(props) {
    //     super(props)
    // }

    render() {
        const {login} = this.state;
        return (
            <ul className={MainSheet.barContainer}>
                <li className={MainSheet.style_logo}>IIPSMT</li>
                <li>模型与系统介绍</li>
                <li>队员介绍</li>
                <li className={MainSheet.btn_language} onClick={this.handleLoginClick}>登录 / 注册</li>
                {login? <Login handleBGClick={this.handleLoginClick}/> : ''}
            </ul>
        )
    }

    handleLoginClick = () => {
        const {login} = this.state;
        this.setState({
            login: !login
        })
    }
}
