import React, { Component } from 'react'
import MainSheet from './index.module.less'

import icon_menu from '../../rsc/icons/menu.svg'

export default class NavigationBar extends Component {
    state = {
        isMobile: false,
        mobileMenu: false
    }

    constructor(props) {
        super(props)
        if (window.innerWidth < 500) {
            this.state = {
                isMobile: true,
                mobileMenu: false,
            }
        }
    }

    render() {
        const { isMobile, mobileMenu } = this.state;
        return (!isMobile ?
            <ul className={MainSheet.barContainer}>
                <li>Authric's Blog</li>
                <li>About Me</li>
                <li>About This Page</li>
                <li className={MainSheet.btn_language}>Languange</li>
            </ul> :
            <div className={MainSheet.mobileContainer}>
                <div className={MainSheet.menuHeader}>
                    <div>Authric's Blog</div>
                    <img src={icon_menu} onClick={this.handleMenuClick} alt={'icon_menu'} />
                </div>
                {
                    mobileMenu ?
                        <ul className={MainSheet.barContainer} onClick={this.handleMenuClick}>
                            <li>About Me</li>
                            <li>About This Page</li>
                            <li className={MainSheet.btn_language}>Languange</li>
                        </ul> : ''
                }
            </div>
        )
    }

    componentDidMount() {
        window.addEventListener('resize', this.handleResize.bind(this))
    }

    handleResize = e => {
        if (e.target.innerWidth < 500) {
            this.setState({
                isMobile: true,
            })
        } else {
            this.setState({
                isMobile: false,
                mobileMenu: false,
            })
        }
    }

    handleMenuClick = () => {
        const { mobileMenu } = this.state;
        this.setState({
            mobileMenu: !mobileMenu
        })
        console.log(1);

    }
}
