import React, { Component } from 'react'
import { Column } from '@ant-design/charts';


import MainSheet from './index.module.css'


export default class HroizontalBar extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: props.data
        }
        // this.config = {
        //     data: this.state.data,
        //     xField: 'name',
        //     yField: 'value',
        //     label: {
        //         position: 'middle',
        //         style: {
        //             fill: '#FFFFFF',
        //             opacity: 0.6,
        //         },
        //     },
        //     xAxis: {
        //         label: {
        //             autoHide: true,
        //             autoRotate: false,
        //         },
        //     },
        //     meta: {
        //         name: { alias: '家族名' },
        //         value: { alias: '数值' },
        //     },
        // };
    }

    static getDerivedStateFromProps(props) {
        return {
            data: props.data
        }
    }

    // data = [
    //     {
    //         type: '家具家电',
    //         sales: 38,
    //     },
    //     {
    //         type: '粮油副食',
    //         sales: 52,
    //     },
    //     {
    //         type: '生鲜水果',
    //         sales: 61,
    //     },
    //     {
    //         type: '美容洗护',
    //         sales: 145,
    //     },
    //     {
    //         type: '母婴用品',
    //         sales: 48,
    //     },
    //     {
    //         type: '进口食品',
    //         sales: 38,
    //     },
    //     {
    //         type: '食品饮料',
    //         sales: 38,
    //     },
    //     {
    //         type: '家庭清洁',
    //         sales: 38,
    //     }
    // ];



    render() {
        this.config = {
            data: this.state.data,
            xField: 'name',
            yField: 'value',
            label: {
                position: 'middle',
                style: {
                    fill: '#FFFFFF',
                    opacity: 0.6,
                },
            },
            xAxis: {
                label: {
                    autoHide: true,
                    autoRotate: false,
                },
            },
            meta: {
                name: { alias: '家族名' },
                value: { alias: '数值' },
            },
        };
        return (
            <div className={MainSheet.pageContainer}>
                <Column {...this.config} />
            </div>
        )
    }


}
