import React, { Component } from 'react'
import { Column } from '@ant-design/charts';
import axios from 'axios';

import MainSheet from './index.module.css'


export default class index extends Component {
    constructor(props) {
        super(props);
        console.log(0);
        this.getData();
        console.log(12);
    }

    data = [
        {
            type: '家具家电',
            sales: 38,
        },
        {
            type: '粮油副食',
            sales: 52,
        },
        {
            type: '生鲜水果',
            sales: 61,
        },
        {
            type: '美容洗护',
            sales: 145,
        },
        {
            type: '母婴用品',
            sales: 48,
        },
        {
            type: '进口食品',
            sales: 38,
        },
        {
            type: '食品饮料',
            sales: 38,
        },
        {
            type: '家庭清洁',
            sales: 38,
        }
    ];

    config = {
        data: this.data,
        width: 300,
        xField: 'type',
        yField: 'sales',
        label: {
            position: 'middle',
            style: {
                fill: '#FFFFFF',
                opacity: 0.6,
            },
        },
        xAxis: {
            label: {
                autoHide: true,
                autoRotate: false,
            },
        },
        meta: {
            type: { alias: '类别' },
            sales: { alias: '销售额' },
        },
    };

    render() {
        console.log('render');
        return (
            <div className={MainSheet.pageContainer}>
                <Column {...this.config} />
            </div>

        )
    }

    getData = () => {
        axios({
            method: 'POST',
            url: 'http://localhost:9001/',
            timeout: 60 * 1000,
        }).then(response => {
            console.log(response.data, '12312');
        })


        // var request = new XMLHttpRequest();
        // request.open("POST", "http://localhost:9001/");
        // request.addEventListener("load",(response)=>{
        //     console.log(response);
        // })
