import React, { Component } from 'react'
// import { connect } from 'react-redux'
// import { } from 'react-router-dom'

import signIn from './index.module.css'

export default class SignIn extends Component {
    render() {
        return (
            <div >
                <form className={signIn.pageContainer} action="">
                    <div className={signIn.userInfo} >UserID</div>
                    <input className={signIn.userInput} placeholder="Your ID" type="text" onBlur={(e) => { this.handleBlur(e, 'id') }} />
                    <div className={signIn.userInfo}>Password</div>
                    <input className={signIn.userInput} placeholder="Password" type="password" onBlur={(e) => { this.handleBlur(e, 'password') }} />
                    <input className={signIn.signInButton} type="button" value="SignIn" onClick={(e) => { this.handleLoginClick(e) }} />
                    <div className={signIn.forgot}>Forgot your password?</div>
                </form>
            </div>
        )
    }

    handleBlur = async (e, info) => {
        switch (info) {
            case 'id':
                if (e.target.value !== '') {
                    this.setState({
                        userID: e.target.value
                    })
                }
                break;
            case 'password':
                if (e.target.value !== '') {
                    this.setState({
                        password: e.target.value
                    })
                }
                break;

            default:
                break;
        }
    }



}
