import React, { Component } from 'react'
import { Bar } from '@ant-design/charts';


import MainSheet from './index.module.css'


export default class HroizontalBar extends Component {
    constructor(props) {
        super(props)
        this.state = {
            data: [{ col_labels: "dreambot123", corr_values: 0 }]
        }
    }

    static getDerivedStateFromProps(props) {
        return {
            data: props.data
        }
    }

    render() {
        this.config = {
            data: this.state.data,
            xField: 'corr_values',
            yField: 'col_labels',
            legend: { position: 'top_left' },
            label: {
                position: 'middle',
                style: {
                    fill: '#FFFFFF',
                    opacity: 0.6,
                },
            },
            xAxis: {
                label: {
                    autoHide: true,
                    autoRotate: false,
                },
            },
            meta: {
                corr_values: { alias: '家族名' },
                col_labels: {
                    alias: '数值',
                    // max: 4000,
                    // min: -4000
                },
            },
        };
        return (
            <div className={MainSheet.pageContainer}>
                <Bar {...this.config} />
            </div>
        )
    }


}
